import React from 'react';
import './App.css';

function App() {
  class Counter extends React.Component {
    constructor (){
      super()
      this.state = {
        score: 0
      };
    }

    decrementScore(){
      this.setState({
        score: this.state.score - 1
      })
    }

    incrementScore(){
      this.setState({
        score: this.state.score + 1
      });
    }

    render (){
      return (
        <div className="counter">
          <button className="counter-action decrement" onClick={() => this.decrementScore() }> - </button>
          <span className="counter-score">{ this.state.score }</span>
          <button className="counter-action increment" onClick={() => this.incrementScore() }> + </button>
        </div>
      );
    }
  };

  const Header = (props) => {
    console.log(props);
    if (props.totalPlayers < 1) {
      return(
        <header className="App-header">
          <div>Sorry, you can't play without players!</div>
        </header>
      );
    }

    let players = [];

    for (let i = 1; i <= props.totalPlayers; i++){
      players.push(<Player key={ 'player'+i.toString() } playerName={'Player '+ i } />);
    }

    return(
      <header className="App-header">
        <h1>{ props.title }</h1>
        <span className="stats">Total Players: { props.totalPlayers }</span>
        <div className="player-board">
          { players }
        </div>
      </header>
    );     

  };

  class Player extends React.Component {
    constructor(){
      super()
      this.state = {
        playerName: ""
      }
    }
    changeName(){
      this.setState({
        playerName: "something"
      });
    }

    render(){
      return (
        <div className="player">
          <div className="player-name">
            <input type="text" className="player-name-text" defaultValue={ this.state.playerName } onChange={() => this.changeName(this.value) } />
          </div>
          <Counter id={ this.state.playerName } />
        </div>
      );
    };
  };

  return (
    <div className="App">
      <Header 
        title="Score Tracker" 
        totalPlayers={ prompt("How many players?") } 
      />    
    </div>
  );
}

export default App;
