import React from 'react';
import './App.css';

function App() {
  const Header = (props) => {
    console.log(props);
    return(
      <header className="App-header">
        <h1>{ props.title }</h1>
        <span className="stats">Players: { props.totalPlayers }</span>
        <Player name="Player 1" score="0" />
        <Player name="Player 2" score="0" />
        <Player name="Player 3" score="0" />
        <Player name="Player 4" score="0" />
        <Player name="Player 5" score="0" />
        <Player name="Player 6" score="0" />
      </header>

    );     
  };

  const Counter = (props) => {
    return (
      <div className="counter">
        <button className="counter-action decrement"> - </button>
        <span className="counter-score">{ props.score }</span>
        <button className="counter-action increment"> + </button>
      </div>
    );
  };

  const Player = (props) => {
    return (
        <div className="player">
          <div className="player-name">
            { props.name }
          </div>
          <Counter score={ props.score } />
        </div>
      );
  };

  return (
    <div className="App">
      <Header 
        title="Scoreboard" 
        totalPlayers={ prompt("How many players?") } 
      />    
    </div>
  );
}

export default App;
