import React from 'react';
import './App.css';

function App() {

  const Message = (props) => {
    return(
      <div>Sorry, you can't play without players!</div>
    );
  };

  const Header = (props) => {
    console.log(props);
    if (props.totalPlayers < 1) {
      return(
        <header className="App-header">
          <div>Sorry, you can't play without players!</div>
        </header>
      );
    }

    let players = [];

    for (let i = 1; i <= props.totalPlayers; i++){
      players.push(<Player key={ i } playerNumber={ i } score={ 0 } />);
    }

    return(
      <header className="App-header">
        <h1>{ props.title }</h1>
        <span className="stats">Total Players: { props.totalPlayers }</span>
          { players }
      </header>
    );     

  };

  const Counter = (props) => {
    return (
      <div className="counter">
        <button className="counter-action decrement"> - </button>
        <span className="counter-score">{ props.score }</span>
        <button className="counter-action increment"> + </button>
      </div>
    );
  };

  const Player = (props) => {
    return (
        <div className="player">
          <div className="player-name">
            Player { props.playerNumber }:
          </div>
          <Counter score={ props.score } id={ props.playerNumber } />
        </div>
      );
  };

  return (
    <div className="App">
      <Header 
        title="Score Tracker" 
        totalPlayers={ prompt("How many players?") } 
      />    
    </div>
  );
}

export default App;
